import { Component, ViewChild, ElementRef } from '@angular/core';
import html2canvas from 'html2canvas';

@Component({
  selector: 'app-share',
  templateUrl: './share.component.html',
  styleUrls: ['./share.component.scss'],
})
export class ShareComponent {
  @ViewChild('shareArea', { static: true }) shareArea!: ElementRef;
  shareImage: any;
  afterText = '';
  blobContent!: Blob;

  constructor() {}

  ngOnInit(): void {}

  shareButton() {
    // const shareData = {
    //   title: '測試',
    //   text: '我在測試分享',
    //   url: 'https://www.youtube.com/?gl=TW&hl=zh-tw',
    // };
    let file = new File([this.blobContent], 'image.png', {
      type: this.blobContent.type,
    });
    let filesArray = [file];
    console.log(navigator.canShare({ files: filesArray }));

    if (navigator.canShare && navigator.canShare({ files: filesArray })) {
      navigator
        .share({
          files: filesArray,
        })
        .then(() => {
          this.afterText = '分享成功';
          console.log('success');
        })
        .catch((error) => {
          this.afterText = '分享失敗';
          console.log('error', error);
        });
    } else {
      console.log('失敗');
    }
  }

  changeImg() {
    html2canvas(this.shareArea.nativeElement).then((canvas) => {
      // debugger;
      this.shareImage = canvas.toDataURL();
      console.log('canvas.toDataURL() -->' + this.shareImage);
      // document.body.appendChild(canvas);

      canvas.toBlob((blob) => {
        if (blob) {
          this.blobContent = blob;
          // console.log(this.blobContent);

          // 添加条件判断
          var reader = new FileReader();
          // debugger;
          reader.readAsDataURL(blob);
          reader.onloadend = function () {
            let base64data = reader.result;
            console.log('Base64--> ' + base64data);
          };
        }
      });
    });
  }
}
<div class="flex justify-around">
  <div>
    <p>請先截圖再分享</p>
    <button
      class="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded"
      (click)="changeImg()"
    >
      截圖
    </button>
    <h1>查看截圖</h1>
    <img src="{{ shareImage }}" />
  </div>

  <div class="flex flex-col items-center">
    <div #shareArea class="share">
      <h1>我是要分享的區塊</h1>
      <p>排行榜</p>
      <p>第一名:OOO</p>
      <p>第二名:XXX</p>
      <p>第三名:VVV</p>
      <br />
      <div class="flex justify-center">
        <input type="text" placeholder="可輸入文字" />
      </div>

      <br />
    </div>
    <div class="share_icon flex justify-around">
      <button
        class="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded"
        (click)="shareButton()"
      >
        立即分享
      </button>

      <span style="color: red">{{ afterText }}</span>
    </div>
  </div>
</div>
