import { Component, ViewChild } from '@angular/core';
import {
  NgxSignaturePadComponent,
  NgxSignatureOptions,
} from '@eve-sama/ngx-signature-pad';

@Component({
  selector: 'app-canvas-pen',
  templateUrl: './canvas-pen.component.html',
  styleUrls: ['./canvas-pen.component.scss'],
})
export class CanvasPenComponent {
  @ViewChild('signature') signature!: NgxSignaturePadComponent;

  /** You can see more introduction in the below about NgxSignatureOptions */
  public options: NgxSignatureOptions = {
    backgroundColor: '#e4e4e7',
    penColor: '#3b82f6',
    width: 570,
    height: 300,
    css: {
      'border-radius': '16px',
    },
  };

  /** The begin event of sign */
  onBeginSign(): void {}

  /** The end event of sign */
  onEndSign(): void {}

  clear(): void {
    this.signature.clear();
  }
}
<div class="flex flex-col items-center">
  <ngx-signature-pad
    #signature
    [options]="options"
    (beginSign)="onBeginSign()"
    (endSign)="onEndSign()"
  >
  </ngx-signature-pad>

  <div class="flex justify-start mt-4">
    <button
      (click)="clear()"
      class="mr-4 bg-transparent hover:bg-blue-500 text-blue-700 font-semibold hover:text-white py-2 px-4 border border-blue-500 hover:border-transparent rounded"
    >
      清除
    </button>
    <button
      class="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded"
    >
      送出
    </button>
  </div>
</div>
