import { Component } from '@angular/core';

@Component({
  selector: 'app-normal-cal',
  templateUrl: './normal-cal.component.html',
  styleUrls: ['./normal-cal.component.scss'],
})
export class NormalCalComponent {
  numberArea: string = '0';
  firstNum: string = '';
  secondNum: string = '';
  handle: string = '';
  isTotal: boolean = false;

  constructor() {}

  ngOnInit(): void {}

  equal() {
    console.log('=');
    this.secondNum = this.numberArea;
    let firstNum = Number(this.firstNum);
    let secondNum = Number(this.secondNum);
    switch (this.handle) {
      case '+': {
        let total = firstNum + secondNum;
        this.numberArea = String(total);
        break;
      }
      case '-': {
        let total = firstNum - secondNum;
        this.numberArea = String(total);
        break;
      }
      case '×': {
        let total = firstNum * secondNum;
        this.numberArea = String(total);
        break;
      }
      case '÷': {
        let total = firstNum / secondNum;
        this.numberArea = String(total);
        break;
      }
      default: {
        break;
      }
    }
    this.isTotal = true;
  }

  sum() {
    console.log('+');
    this.init();
    this.handle = '+';
  }

  sub() {
    console.log('-');
    this.init();
    this.handle = '-';
  }

  mul() {
    console.log('×');
    this.init();
    this.handle = '×';
  }
  div() {
    console.log('÷');
    this.init();
    this.handle = '÷';
  }

  init() {
    this.firstNum = this.numberArea;
    this.numberArea = '0';
  }

  clickNumber(num: string) {
    if (this.numberArea === '0' || this.isTotal) {
      this.numberArea = num;
      this.isTotal = false;
    } else {
      this.numberArea = this.numberArea.concat(num);
    }
  }

  delete() {
    if (this.numberArea?.length === 1) {
      this.numberArea = '0';
    } else {
      this.numberArea = this.numberArea.substring(
        0,
        this.numberArea?.length - 1
      );
    }
  }

  reset() {
    this.numberArea = '0';
  }
}
<div class="calculator-box">
  <div class="show-number">
    <p>{{ numberArea | number : "1.0-5" }}</p>
  </div>
  <div class="input-area">
    <div (click)="clickNumber('7')">7</div>
    <div (click)="clickNumber('8')">8</div>
    <div (click)="clickNumber('9')">9</div>
    <div class="other-color" (click)="div()">÷</div>
    <div (click)="clickNumber('4')">4</div>
    <div (click)="clickNumber('5')">5</div>
    <div (click)="clickNumber('6')">6</div>
    <div class="other-color" (click)="mul()">×</div>
    <div (click)="clickNumber('1')">1</div>
    <div (click)="clickNumber('2')">2</div>
    <div (click)="clickNumber('3')">3</div>
    <div class="other-color" (click)="sum()">+</div>
    <div (click)="clickNumber('0')">0</div>
    <div (click)="clickNumber('00')">00</div>
    <div (click)="clickNumber('.')">.</div>
    <div class="other-color" (click)="sub()">-</div>
    <div (click)="reset()">AC</div>
    <div (click)="delete()">del</div>
    <div class="equal" (click)="equal()">=</div>
  </div>
</div>
